import React, { useState } from 'react';
import emailjs from 'emailjs-com';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });

  const handleChange = (event) => {
    setFormData({ ...formData, [event.target.id]: event.target.value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const { name, email, phone, message } = formData;
    const params = { name, email, phone, message };
    const serviceID = 'service_5v104qn';
    const templateID = 'template_74xb314';

    emailjs.send(serviceID, templateID, params)
      .then((res) => {
        setFormData({ name: '', email: '', phone: '', message: '' });
        console.log(res);
        alert('Your message has been sent successfully.');
      })
      .catch((err) => console.log(err));
  };

  return (
    <div className="container" style={{ height: '800px', textAlign: 'left' }}>
      <div className="col">
        <h5 htmlFor="nom">Nom</h5>
        <div className="form-group">
          <input type="text" id="name" placeholder="Nom" required onChange={handleChange} value={formData.name} />
        </div>
      </div>

      <div className="saut0" />

      <div className="col">
        <h5 htmlFor="email">Email</h5>
        <div className="form-group">
          <input type="text" id="email" placeholder="Email" required onChange={handleChange} value={formData.email} />
        </div>
      </div>

      <div className="saut0" />

      <div className="col">
        <h5 htmlFor="numéro">Numéro de téléphone</h5>
        <div className="form-group">
          <input type="text" id="phone" placeholder="Numéro de téléphone" required onChange={handleChange} value={formData.phone} />
        </div>
      </div>

      <div className="saut0" />

      <div className="col">
        <h5 htmlFor="message">Message</h5>
        <div className="form-group">
          <textarea id="message" rows="10" placeholder="Votre message" onChange={handleChange} value={formData.message} />
        </div>
      </div>

      <div className="saut0" />

      <div className="centrer">
        <button style={{ textAlign: 'center' }} className="btn btn-danger" onClick={handleSubmit}>Envoyer</button>
      </div>
    </div>
  );
};

export default ContactForm;
