import React from "react";
import Logo from "../components/Logo";
//Appel la Navigation pour faire fonctionner la navigation
import Navigation from "../components/Navigation";
import "bootstrap/dist/css/bootstrap.css";

const Contact = () => {
  return (
    <div>
      <Logo />
      <Navigation />
      <br></br>
      <div class="moi" style={{ height: "50px", width: "43.2%" }}>
        <d>
          <b>Contact</b>
        </d>
        <c> Me</c>
      </div>

      <div class="container" style={{ height: "800px", textAlign: "center" }}>
        <div className="row">
          <div class="col">
            <h5 for="nom" s>
              {" "}
              Nom{" "}
            </h5>
            <input type="text" id="name" placeholder="Nom" required />
          </div>

          <div class="col">
            <h5 for="prenom" s>
              {" "}
              Prénom{" "}
            </h5>

            <input type="text" id="prenom" placeholder="Prénom" required />
          </div>
        </div>

        <div class="saut0"></div>
        <div className="row">
          <div class="col">
            <h5 for="email"> Email </h5>

            <input type="text" id="email" placeholder="Email" required />
          </div>

          <div class="col">
            <h5 for="numéro"> Numéro de téléphone </h5>

            <input
              type="text"
              id="phone"
              placeholder="Numéro de téléphone"
              required
            />
          </div>
        </div>
        <div class="saut0"></div>
        <div class="col">
          <h5 for="message"> Message </h5>
          <div class="from-group">
            <textarea id="message" rows="10" placeholder="Votre message" />
          </div>
        </div>
        <div class="saut0"></div>
        <div class="centrer">
          <button style={{ textAlign: "center" }} onclick="sendMail()">
            Envoyer
          </button>
        </div>
      </div>
    </div>

    
  );
  
};
//Send Email Form JS
function sendMail(){
  var params = {
    name: document.getElementById("name").value,
    email: document.getElementById("email").value,
    phone: document.getElementById("phone").value,
    message: document.getElementById("message").value,
  };

const serviceID ="service_5v104qn";
const templateID="template_74xb314";

emailjs
.send(serviceID, templateID, params) 
.then((res)=>{
    document.getElementById("name").value ="";
    document.getElementById("email").value ="";
    document.getElementById("phone").value ="";
    document.getElementById("message").value ="";
    console.log(res);
    alert("your message sent successfully");
})
.catch((err) => console.log(err));

};

componentDidMount = () => {
  const script1 = document.createElement("script");
  script1.src = "https://cdn.jsdelivr.net/npm/@emailjs/browser@3/dist/email.min.js";
  script1.type = "text/javascript";
  document.body.appendChild(script1);

  const script2 = document.createElement("script");
  script2.type = "text/javascript";
  script2.innerHTML = 
    `(function(){
      emailjs.init("xOjTc2czq_HwhNANc");
    })();
  `;
  document.body.appendChild(script2);
}



export default Contact;
