import React from "react";
import Logo from "../components/Logo";
//Appel la Navigation pour faire fonctionner la navigation
import Navigation from "../components/Navigation";
import "bootstrap/dist/css/bootstrap.css";

const Contact = () => {
  return (
    <div>
      <Logo />
      <Navigation />
      <br></br>
      <div class="moi" style={{ height: "50px", width: "43.2%" }}>
        <d>
          <b>Contact</b>
        </d>
        <c> Me</c>
      </div>

      <div className="container" style={{ height: 800, textAlign: "left" }}>
      <div className="col">
        <h5 htmlFor="nom">Nom</h5>
        <div className="from-group">
          <input
            type="text"
            id="name"
            placeholder="Nom"
            required
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
      </div>

      <div className="saut0"></div>

      <div className="col">
        <h5 htmlFor="email">Email</h5>
        <div className="from-group">
          <input
            type="text"
            id="email"
            placeholder="Email"
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
      </div>

      <div className="saut0"></div>

      <div className="col">
        <h5 htmlFor="numéro">Numéro de téléphone</h5>
        <div className="from-group">
          <input
            type="text"
            id="phone"
            placeholder="Numéro de téléphone"
            required
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>
      </div>
      <div className="saut0"></div>
      <div className="col">
        <h5 htmlFor="message">Message</h5>
        <div className="from-group">
          <textarea
            id="message"
            rows="10"
            placeholder="Votre message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
        </div>
      </div>
      <div className="saut0"></div>
      <div className="centrer">
        <button
          style={{ textAlign: "center" }}
          className="btn btn-danger"
          onClick={handleSubmit}
        >
          Envoyer
        </button>
      </div>
    </div>

    </div>

    
  );
  
};
//Send Email Form JS
function sendMail(){
  var params = {
    name: document.getElementById("name").value,
    email: document.getElementById("email").value,
    phone: document.getElementById("phone").value,
    message: document.getElementById("message").value,
  };

const serviceID ="service_5v104qn";
const templateID="template_74xb314";

emailjs
.send(serviceID, templateID, params) 
.then((res)=>{
    document.getElementById("name").value ="";
    document.getElementById("email").value ="";
    document.getElementById("phone").value ="";
    document.getElementById("message").value ="";
    console.log(res);
    alert("your message sent successfully");
})
.catch((err) => console.log(err));

};





export default Contact;
