import React, { useState } from "react";

function ContactForm() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [message, setMessage] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
    // Your sendMail function here
  }

  return (
    <div className="container" style={{ height: 800, textAlign: "left" }}>
      <div className="col">
        <h5 htmlFor="nom">Nom</h5>
        <div className="from-group">
          <input
            type="text"
            id="name"
            placeholder="Nom"
            required
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
      </div>

      <div className="saut0"></div>

      <div className="col">
        <h5 htmlFor="email">Email</h5>
        <div className="from-group">
          <input
            type="text"
            id="email"
            placeholder="Email"
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
      </div>

      <div className="saut0"></div>

      <div className="col">
        <h5 htmlFor="numéro">Numéro de téléphone</h5>
        <div className="from-group">
          <input
            type="text"
            id="phone"
            placeholder="Numéro de téléphone"
            required
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>
      </div>
      <div className="saut0"></div>
      <div className="col">
        <h5 htmlFor="message">Message</h5>
        <div className="from-group">
          <textarea
            id="message"
            rows="10"
            placeholder="Votre message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
        </div>
      </div>
      <div className="saut0"></div>
      <div className="centrer">
        <button
          style={{ textAlign: "center" }}
          className="btn btn-danger"
          onClick={handleSubmit}
        >
          Envoyer
        </button>
      </div>
    </div>
  );
}

export default ContactForm;
